import React, { useEffect, useRef, useState, useCallback } from "react";
import { Chessground } from "chessground";
import { Chess } from "chess.js";
import StockfishEngine from "./StockfishEngine";
import "../../node_modules/chessground/assets/chessground.base.css";
import "../../node_modules/chessground/assets/chessground.brown.css";
import "../../node_modules/chessground/assets/chessground.cburnett.css";

const ChessBoard = () => {
  const boardRef = useRef(null);
  const chess = useRef(new Chess());
  const groundRef = useRef(null); // Store Chessground instance
  const [topMoves, setTopMoves] = useState([]); // Store Stockfish's top moves

  // Create a callback for the evaluation
  const handleEvaluation = useCallback((data) => {
    const parsedMoves = parseStockfishOutput(data);
    setTopMoves(parsedMoves);
  }, []);

  // Initialize Stockfish Engine - Fix the usage
  const engine = StockfishEngine({
    onEvaluation: handleEvaluation
  });

  // Initialize Chessground
  useEffect(() => {
    if (!groundRef.current) {
      groundRef.current = Chessground(boardRef.current, {
        fen: chess.current.fen(),
        orientation: "white",
        movable: {
          free: false,
          dests: getDests(),
          events: {
            after: (orig, dest) => handleMove(orig, dest),
          },
        },
        highlight: {
          lastMove: true,
          check: true,
        },
      });
    }

    // Cleanup on unmount
    return () => {
      if (groundRef.current) {
        groundRef.current.destroy();
      }
    };
  }, []); // Run only once on mount

  // Handle player moves
  const handleMove = (orig, dest) => {
    const move = chess.current.move({ from: orig, to: dest, promotion: "q" });

    if (move === null) {
      console.log("Invalid move");
      return "snapback";
    }

    // Update the board state
    groundRef.current.set({ fen: chess.current.fen() });

    // Trigger Stockfish analysis
    analyzePosition();
  };

  // Get possible legal moves
  const getDests = () => {
    const dests = new Map();
    chess.current.moves({ verbose: true }).forEach((move) => {
      if (!dests.has(move.from)) dests.set(move.from, []);
      dests.get(move.from).push(move.to);
    });
    return dests;
  };

  // Use engine.sendCommand instead of destructuring
  const analyzePosition = () => {
    engine.sendCommand(`position fen ${chess.current.fen()}`);
    engine.sendCommand("go depth 15");
  };

  // Parse Stockfish output for best moves
  const parseStockfishOutput = (output) => {
    const moveRegex = /pv\s+([a-h][1-8][a-h][1-8])\s/g;
    let moves = [];
    let match;
    while ((match = moveRegex.exec(output)) !== null) {
      moves.push(match[1]);
    }
    return moves.slice(0, 5); // Get top 5 moves
  };

  return (
    <div style={{ display: "flex" }}>
      {/* Chessboard */}
      <div ref={boardRef} style={{ width: "400px", height: "400px" }}></div>

      {/* Sidebar for Stockfish Analysis */}
      <div style={{ width: "200px", marginLeft: "20px" }}>
        <h3>Top Moves</h3>
        <ul>
          {topMoves.map((move, index) => (
            <li key={index}>{move}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default ChessBoard;
