import { useEffect, useState, useRef } from "react";

const StockfishEngine = ({ onEvaluation }) => {
    const [engine, setEngine] = useRef(null);

    useEffect(() => {
        const stockfish = new Worker("/stockfish/stockfish-16.1.js");
        engineRef.current = stockfish;

        const handleMessage = (event) => {
            if (event.data.includes("bestmove") || event.data.includes("score")) {
              console.log("Stockfish output:", event.data);
              onEvaluation(event.data); // Send analysis results to the parent component
            }
          };

        stockfish.addEventListener("message", handleMessage);
        stockfish.postMessage("uci");

        return () => {
            stockfish.removeEventListener("message", handleMessage);
            stockfish.terminate();
        };
    }, [onEvaluation]);

    const sendCommand = (cmd) => {
        if (engine) {
            console.log("Sending command to Stockfish:", cmd);
            console.log("yo is something happening here");
            engineRef.current.postMessage(cmd);
        }
    };

    return { sendCommand };
};

export default StockfishEngine;
