import React, { useEffect, useRef } from 'react';
import { Chessground } from 'chessground';
import { Chess } from 'chess.js';

const ChessBoard = () => {
  const boardRef = useRef(null);
  const chess = useRef(new Chess());

  useEffect(() => {
    const config = {
      fen: chess.current.fen(),
      orientation: 'white',
      movable: {
        color: 'white',
        free: false,
        dests: getDests(chess.current),
        events: {
          after: (orig, dest) => {
            const move = chess.current.move({ from: orig, to: dest, promotion: 'q' });
            if (move === null) return 'snapback'; // illegal move

            ground.set({ 
              fen: chess.current.fen(),
              movable: {
                dests: getDests(chess.current)
              }
            });
          }
        }
      }
    };

    const ground = Chessground(boardRef.current, config);

    return () => {
      ground.destroy();
    };
  }, []);

  function getDests(chess) {
    const dests = new Map();
    const moves = chess.moves({ verbose: true });

    moves.forEach(move => {
      if (!dests.has(move.from)) {
        dests.set(move.from, []);
      }
      dests.get(move.from).push(move.to);
    });

    return dests;
  }

  return <div ref={boardRef} style={{ width: '400px', height: '400px' }}></div>;
};

export default ChessBoard;
